mapper.update:

update  exam_paper set p_title=#{pTitle} ,pc_id=#{pcId} ,p_start_time=#{pStartTime},
p_end_time=#{pEndTime} ,p_free=#{pFree}, p_status=#{pStatus}

    ,p_tol_score=#{pTolScore}

where p_id=#{pId}

mapper.select:
select count(a.ques_score) from (
    select exam_papques.pq_id,exam_papques.p_id,exam_papques.ques_id,exam_question.ques_score from exam_papques LEFT JOIN exam_question on exam_papques.ques_id=exam.exam_papques.ques_id
)as a
where a.p_id=#{value};
